import React, {useEffect, useState} from 'react';
import './App.css';

function App() {
  const [colorValue, setColorValue] = useState('#000000')
  const [RGBValue, setRGBValue] = useState('rgb(0, 0, 0)')
  const [CMYKValue, setCMYKValue] = useState('cmyk(0%, 0%, 0%, 100%)')
  const [HLSValue, setHLSValue] = useState('hls(0, 0%, 0%)')

  const [RGBValid, setRGBValid] = useState(true)
  const [CMYKValid, setCMYKValid] = useState(true)
  const [HLSValid, setHLSValid] = useState(true)

  useEffect(() => {
    let res = hexToRgb(colorValue)

    setRGBValue(`rgb(${res.r}, ${res.g}, ${res.b})`)
  }, [colorValue])

  const hexToRgb = (hex) => {
    let result = /^#?([a-f\d]{2})([a-f\d]{2})([a-f\d]{2})$/i.exec(hex);
    return result ? {
      r: parseInt(result[1], 16),
      g: parseInt(result[2], 16),
      b: parseInt(result[3], 16)
    } : null;
  }

  const rgbToHex= (r, g, b) => {
    function componentToHex(c) {
      let hex = c.toString(16);
      return hex.length == 1 ? "0" + hex : hex;
    }

    return "#" + componentToHex(r) + componentToHex(g) + componentToHex(b);
  }

  const colorInput = (e) => {
    setColorValue(e.target.value)
  }

  return (
    <div className="App">
      <h1>RGB, CMYK, HLS</h1>
      <div>
        <input type='color' value={colorValue} onInput={colorInput}/>
      </div>
      <div>
        <span>RGB</span>
        <input type='text' className={`${!RGBValid ? 'error' : ''}`} value={RGBValue} onChange={(e) => {
          setRGBValue(e.target.value)
          let splited

          try {
            splited = /^rgb\(1?[0-9]{1, 2}|2[0-4][0-9]|25[0-5]\)$/.exec(e.target.value)

            splited = e.target.value.split('rgb')[1].split('(')[1].split(')')[0].split(',')
            console.log(splited)
            setColorValue('#121212')
            setRGBValid(true)
          } catch (error) {
            setRGBValid(false)
          }
          }}/>
      </div>
      <div>
        <span>CMYK</span>
        <input type='text' className={''} value={CMYKValue} onChange={(e) => {setCMYKValue(e.target.value)}}/>
      </div>
      <div>
        <span>HLS</span>
        <input type='text' value={HLSValue} onChange={(e) => {setHLSValue(e.target.value)}}/>
      </div>
    </div>
  );
}

export default App;
